import React,{useState,useEffect} from 'react';
import{useHistory,useLocation} from "react-router-dom";
import axios from 'axios';


   const initialState={

    name:"",
    packId:"",
    destination:"",
    numofdays:"",
    nopass:"",
    hotel:"",
    transport:"",
    tourGuide:"",
    totPrice:"",




   };


const AddPack =()=>{
   
    const[state, setState]=useState(initialState);
    const{name,packId,destination,numofdays,nopass,hotel,transport,tourGuide,totPrice}=state;

    return(


     <div style={{marginTop:"100px"}}>
         <form>
            style={{
                   margin:"auto",
                   padding:"15px",
                   maxWidth:"400px",
                   alignContent:"center",

            }}

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={name}
            
            
            </input>
            

            <label htmlFor="name">Package Id</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package ID",
                onChange={handleInputChange}
                value={packId}
            
            
            </input>



            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={destination}
            
            
            </input>


            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={numofdays}
            
            
            </input>

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={nopass}
            
            
            </input>

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={hotel}
            
            
            </input>

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={transport}
            
            
            </input>

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={tourGuide}
            
            
            </input>

            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={totPrice}
            
            
            </input>
            






         </form>
        
     </div>


    )




}
export default AddPack;
