import React,{Component} from 'react';
import axios from 'axios';


export default class EditPackage extends Component{

    constructor(props){
       super(props);
       this.onChangePackageID = this.onChangePackageID.bind(this);
       this.onChangePackName=this.onChangePackName.bind(this);
       this.onChangeDestination=this.onChangeDestination.bind(this);
       this.onChangeNumOfDays=this.onChangeNumOfDays.bind(this);
       this.onChangeNumOfPass=this.onChangeNumOfPass.bind(this);
       this.onChangeHotel=this.onChangeHotel.bind(this);
       this.onChangeTransport=this.onChangeTransport.bind(this);
       this.onChangeGuide=this.onChangeGuide.bind(this);
       this.onChangeTot=this.onChangeTot.bind(this);
       this.onSubmit = this.onSubmit.bind(this);

       this.state={
  
       name: '',
       packId : '',
       destination : '',
       numofdays : '',
       nopass : '',
       hotel : '',
       transport : '',
       tourGuide : '',
       totPrice : ''

       }
       
    } 
     componentDidMount(){

      axios.get('http://localhost:8070/package/edit'+this.props.match.params.id)
         .then(response=>{

         this.setState({
 
          name:response.data.name,
          packId:response.data.packId,
          name:response.data.name,
          destination:response.data.destination,
          numofdays:response.data.numofdays,
          nopass:response.data.nopass,
          hotel:response.data.hotel,
          transport:response.data. transport,
          tourGuide:response.data.tourGuide,
          totPrice:response.data.totPrice,
             
         });

         })
          
         .catch(function(error){

           console.log(error);


         })


         


     }
     
     onChangePackName(e){
      this.setState({
          name:e.target.value
      
      });
    }

    onChangePackageID(e){
      this.setState({
        packId:e.target.value
      
      });
    }

    onChangeDestination(e){
      this.setState({
        destination:e.target.value
      
      });
    }

    onChangeNumOfDays(e){
      this.setState({
        numofdays:e.target.value
      
      });
    }

    onChangeNumOfPass(e){
      this.setState({
        nopass:e.target.value
      
      });
    }


    onChangeHotel(e){
      this.setState({
        hotel:e.target.value
      
      });
    }



    onChangeTransport(e){
      this.setState({
        transport:e.target.value
      
      });
    }


    onChangeGuide(e){
      this.setState({
        tourGuide:e.target.value
      
      });
    }


    onChangeTot(e){
      this.setState({
        totPrice:e.target.value
      
      });
    }
   
    onSubmit(e){
    
       e.preventDefault();
       const obj = {

          name:this.state.name,
          packId:this.state.packId,
          destination:this.state.destination,
          numofdays:this.state. numofdays,
          nopass:this.state.nopass,
          hotel:this.state. hotel ,
          transport:this.state. transport,
          tourGuide:this.state.tourGuide,
          totPrice:this.state. totPrice ,
          
       };

       axios.post('http://localhost:8070/package/update'+ this.props.match.params.id,obj)

       .then(res=>console.log(res.data));
       this.props.history.push('/index');


    }




















   render(){

     return(

     <div>


      <h3>This is Edit class</h3>


     </div>



     )
     


   }



}
