import React,{useEffect, useState} from "react"
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";
import tour from '../images/mirissa.jpg'



export default function editPack(){

    const [name,setName] = useState("");
    const [packId,setPackID] = useState("");
    const [destination,setDesti] = useState("");
    const [numofdays,setDys] = useState("");
    const [nopass,setNopass] = useState("");
    const [hotel,setHotel] = useState("");
    const [transport,setTrans] = useState("");
    const [tourGuide,setGuide] = useState("");
    const [totPrice,setPrice] = useState("");


  const { id } = useParams();

  useEffect(() => {
      getPacks();
    }, []);
  
    //let navigate= useNavigate();
  
    function getPacks() {
      let Pack = true;
  
      fetch(`http://localhost:8070/package/get/${id}`)
        .then((res) => res.json())
  
        .then((result) => {
          if (Pack) {
            setName(result.name);
            setPackID(result.packId);
            setDesti(result.destination);
            setDys(result.numofdays);
            setNopass(result.nopass);
            setHotel(result.hotel);
            setTrans(result.transport);
            setGuide(result.tourGuide);
            setPrice(result.totPrice);
     
           
          }
          console.log(result);
        });
  
      return () => (Pack = false);
    }

  
  function updateData(e){

      e.preventDefault();

      //alert("Insert");

      const updatepack ={
        name,
        packId,
        destination,
        numofdays,
        nopass,
        hotel,
        transport,
        tourGuide,
        totPrice
      
      }

      axios
    .patch(`http://localhost:8070/package/update/${id}`, updatepack)
    .then((_res) => {
      alert("Package Updated Successfully!");
     // navigate("/front");
      console.log(updatepack);
    })
    .catch((_err) => {
      
      alert("Database Error");
    });
  }



return(
    <div className="container" class="p-3 mb-2 bg-dark text-white">
        <h2 class="p-3 mb-2 bg-success text-dark"><center>Update Package Details</center></h2>
        <form className =  "CusPackages" >
          
        <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputEmail4">Package Name</label>
                    <input type="text" className="form-control" id="uname" placeholder="Package Name" value={name} onChange={(e)=>{setName(e.target.value);}}/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="iinputPid">Package ID</label>
                    <input type="email" className="form-control" id="Pid" placeholder="PXXXX" value={packId} onChange={(e)=>{setPackID(e.target.value);} }/>
                </div>
            </div>

            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputSugges">Destination</label>
                    <input type="text" className="form-control" id="suggest" placeholder="Enter destination" value={destination} onChange={(e)=>{setDesti(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputFande">numofdays</label>
                    <input type="text" className="form-control" id="fande" placeholder="E.g-2,3,5" value={numofdays} onChange={(e)=>{setDys(e.target.value);} }/>
                </div>
            </div>

          
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">NumberofPassengers</label>
                    <input type="date" className="form-control" id="rate" placeholder="Eg-3,4,6" value={nopass} onChange={(e)=>{setNopass(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Hotel</label>
                    <input type="text" className="form-control" id="date" placeholder="Hotel name" value={hotel}  onChange={(e)=>{setHotel(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Transport</label>
                    <input type="text" className="form-control" id="rate" placeholder="Pransport servise company." value={transport} onChange={(e)=>{setTrans(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Tour guide(with/without)</label>
                    <input type="text" className="form-control" id="date" placeholder="with/without" value={tourGuide}  onChange={(e)=>{setGuide(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Price</label>
                    <input type="text" className="form-control" id="rate" placeholder="Enter price here" value={totPrice} onChange={(e)=>{setPrice(e.target.value);} }/>
            </div></div>

        
            
        </form><br></br><div>
                 <a href="/allCuz" class="btn btn-success" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Priviouse Page</strong></a>&nbsp;&nbsp;
                <button  type="button" class="btn btn-danger"   onClick={updateData}> <i className="fa-solid fa-wrench "></i>&nbsp;&nbsp; Update My Package&nbsp;&nbsp;&nbsp;</button>&nbsp;&nbsp;
            
            </div>
            
            
    </div>
)

}
