import React,{Component} from "react";
import axios from "axios";
import {Link} from 'react-router-dom';

//import Package from "../../../TourPackage/BACKEND/models/Package";



export default class S_AllCusPacz extends Component{


   constructor(props){

       super(props);

       this.state={

       cusPacks:[]
       };

   }
  componentDidMount(){

   this.retriveCusPacks();
  }
  retriveCusPacks(){

   axios.get("http://localhost:8070/cusPack/all4").then(res=>{

      if(res.data.success){
          this.setState({

            cusPacks:res.data.existingCusPacks

          });

          console.log(this.state.cusPacks)

      }


   })



  }



  
  
  onDelete(id){

     fetch(`http://localhost:8070/cusPack/delete/${id}`,{

           method:`DELETE`




     }).then((result)=>{

         result.json().then((resp)=>{

           console.warn(resp)
           alert("Package Cancelled...!!")
           this.retriveCusPacks()

         })


     }) 
    


  }

  //Filter /Search Mechod
 filterContent(cusPacks,searchTerm){

  const results=cusPacks.filter((cusPacks)=>cusPacks.name.toLowerCase().includes(searchTerm));
  this.setState({cusPacks:results});

}



handleTextSearch=(e)=>{

   const searchTerm=e.currentTarget.value;
   axios.get("http://localhost:8070/cusPack/all4").then(res=>{

    if(res.data.success){
       this.filterContent(res.data.existingCusPacks,searchTerm)
    }
});

}; 





  
 render(){

    return(


      <div className="container" class="p-3 mb-2 bg-secondary text-white">
         

<div className="row">
       <div className="col-lg-9 mt-2 mb-2">
          <h3><strong>Find your tour package</strong>-</h3>
          

       </div>
       
       <div className="col-lg-3 mt-2 mb-2" class="text-center" style={{width:400}}>
          
        
             <input
              className="form-control"
              type="search"
              placeholder="Enter User Name Here"
              name="searchTerm"
              onChange={this.handleTextSearch}
             
             ></input>


       </div>

       </div>
      <br></br>


     
      <table class="table table-dark table-hover">
      <thead>
      <tr class="text-danger">
          <th scope="col">Count</th>
           <th scope="col">Name</th>
           <th scope="col">Email</th>
           <th scope="col">Phone</th>
           <th scope="col">Country</th>
           <th scope="col">Arrival Date</th>
           <th scope="col">Pick Up Place</th>
           <th scope="col">Number Of Adults</th>
           <th scope="col">Number Of Childs</th>
           <th scope="col">Duration(Days)</th>
           <th scope="col">Notes</th>
           <th scope="col">Action</th>

        
  
      </tr>
     </thead>
     <tbody>

       {this.state.cusPacks.map((cusPacks,index)=>(

        <tr>
          <th scope="row">{index+1}</th>
          <td>{cusPacks.name}</td>
          <td>{cusPacks.email}</td>
          <td>{cusPacks.phone}</td>
          <td>{cusPacks.country}</td>
          <td>{cusPacks.arriDate}</td>
          <td>{cusPacks.pickPlace}</td>
          <td>{cusPacks.numofAdd}</td>
          <td>{cusPacks.numOfChild}</td>
          <td>{cusPacks.duration}</td>
          <td>{cusPacks.notes}</td>
      
          <td>
                                <a className="btn btn-warning"  href={`/updCuz/${cusPacks._id}`}>Update</a>
                                </td>
         <td> <button type="button" class="btn btn-success" onClick={()=>this.onDelete(cusPacks._id)}><i className="fa-solid fa-trash-can "></i>&nbsp;&nbsp;Cancel Package</button></td>
         

        </tr>

       ))}



     </tbody>



     </table>
     <a href="/cuz" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Previous Page</strong></a>
      </div>

    

    ) 
 }

 }
