import React, {useState, useEffect} from 'react';
import axois from "axios";

export default function AllPackages(){

  const [packages,setPackages]=useState([]);

 useEffect(()=>{
 
  function getPackages(){

   axois.get("http://localhost:8070/package/").then((res)=>{

    setPackages(res.data);

   }).catch((err)=>{

    alert(err.message);

   })


  }

  getPackages()
 }, [])










  return(

  <div>

   {this.state.packages.map(packages=>(

    <div>
   <p>{packages.name}</p>   
   <p>{packages.packId}</p>  
   <p>{packages.destination}</p>  
   <p>{packages.numofdays}</p>  
   <p>{packages.nopass}</p>  
   <p>{packages.hotel}</p>  
   <p>{packages.transport}</p>  
   <p>{packages.tourGuide}</p>  
   <p>{packages.totPrice}</p>  
    </div>


   ))}


  </div>


  )





}
