import React from "react";
import axios from 'axios';
//import '../App.css';
//import "bootstrap/dist/css/bootstrap.min.css";
/*
function updatePackage() {
 export default class editPack extends Component{

  constructor(props){
    super(props);
    this.onChangeFullName = this.onChangeFullName.bind(this);
    this.onChangeCardNum = this.onChangeCardNum.bind(this);
    this.onChangeExpiryDate = this.onChangeExpiryDate.bind(this);
    this.onChangeCvv = this.onChangeCvv.bind(this)
    this.onChangeStreetAddress = this.onChangeStreetAddress.bind(this)
    this.onChangeCountry = this.onChangeCountry.bind(this)
    this.onChangeCity = this.onChangeCity.bind(this)
    this.onChangeZip = this.onChangeZip.bind(this)
    this.onChangeState = this.onChangeState.bind(this)
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      payments:[]
      // card_num: '',
      // full_name: '',
      // cvv: '',
      // expiry_date: '',
      // street_address: '',
      // country: '',
      // city: '',
      // zip_code: '',
      // state: ''
    }
  }

  componentDidMount() {
    this.retrivePackages();
  }

  retrivePackages(){
    axios.get(`http://localhost:8070/payment/get/${this.props.user.id}`)
      .then(response => {
        console.log(response.data);
        this.setState({payments:response.data.payment})
      })
      .catch(function (error){
        console.log(error);
    })
  }

  onChangeFullName(e){
    this.setState({
      full_name: e.target.value
    });
  }

  onChangeCardNum(e){
    this.setState({
      card_num: e.target.value
    });
  }

  onChangeExpiryDate(e){
    this.setState({
      expiry_date: e.target.value
    });
  }

  onChangeCvv(e){
    this.setState({
      cvv: e.target.value
    });
  }

  onChangeStreetAddress(e){
    this.setState({
      street_address: e.target.value
    });
  }

  onChangeCountry(e){
    this.setState({
      country: e.target.value
    });
  }

  onChangeCity(e){
    this.setState({
      city: e.target.value
    });
  }

  onChangeZip(e){
    this.setState({
      zip: e.target.value
    });
  }

  onChangeState(e){
    this.setState({
      state: e.target.value
    });
  }

  onSubmit(e){
    e.preventDefault();
    const obj = {
      user_id: this.props.user.id,
      card_num: this.state.card_num,
      full_name: this.state.full_name,
      cvv: this.state.cvv,
      expiry_date: this.state.expiry_date,
      street_address: this.state.street_address,
      country: this.state.country,
      city: this.state.city,
      zip_code: this.state.zip_code,
      state: this.state.state
    }

    axios.put("http://localhost:8070/payment/update",obj).then(()=>{
      alert("Package Updated!")
    }).catch((err)=>{
        alert(err)
    })
  }


 render() {
  return (
    <><div className="App">
          <h1>Details can update from here.</h1>
      </div><div>
              <div class="card p-3 border-0 horizontal-center" style={{ maxWidth: "70%" }}>
                  <h2 class="text-center margin-bottom"><strong>Enter payment details</strong></h2>
