const editPack = () => {
    const [roomType, setroomType] = useState("A/C");
    const [numberOfRoom, setnumberOfRoom] = useState("");
    const [numberOfGuest, setnumberOfGuest] = useState("");
    const [priceOfOnePerson, setpriceOfOnePerson] = useState("");
    const [availableBeds, setavailableBeds] = useState("");
    const [numberOfBeds, setnumberOfBeds] = useState("");
  
    const history = useNavigate();
    const { id } = useParams();
  
    useEffect(() => {
      (async () =>
        await axios.get(`/room/get/${id}`).then((res) => {
          setroomType(res.data.roomType);
          setnumberOfRoom(res.data.numberOfRoom);
          setnumberOfGuest(res.data.numberOfGuest);
          setnumberOfBeds(res.data.numberOfBeds);
          setpriceOfOnePerson(res.data.priceOfOnePerson);
          setavailableBeds(res.data.availableBeds);
        }))();
    }, []);
  
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      try {
        await axios.put(`/room/update/${id}`, {
          roomType,
          numberOfRoom,
          numberOfGuest,
          priceOfOnePerson,
          availableBeds,
          numberOfBeds,
        });
        alert("Successfully Room Updated");
      } catch (error) {
        alert(error);
      }
    };
    return (
      <div className="container mx-auto mt-4">
        <form onSubmit={handleSubmit}>
          <div className=" border-2 my-4">
            <div>
              <h2 className="text-white ml-2 mt-2 mb-2">Room Type</h2>
              <div className="ml-6 mt-4">
                <RadioGroup
                  row
                  aria-labelledby="demo-row-radio-buttons-group-label"
                  name="row-radio-buttons-group"
                  onChange={(e) => setroomType(e.target.value)}
                  value={roomType}
                >
                  <div className=" border-2 py-1 px-2 rounded-lg">
                    <FormControlLabel
                      value="A/C"
                      control={<Radio />}
                      label="A/C"
                    />
                  </div>
                  <div className=" border-2 py-1 px-2 rounded-lg translate-x-4">
                    <FormControlLabel
                      value="Non A/C"
                      control={<Radio />}
                      label="Non A/C"
                    />
                  </div>
                </RadioGroup>
              </div>
            </div>
            <div className="mb-4 mt-4">
              <h2 className="text-white ml-2 mt-2 mb-2">
                Number of rooms of this type?
              </h2>
              <TextField
                style={{ width: "600px", marginLeft: "10px" }}
                size="small"
                id="outlined-basic"
                label="Enter Number of Room"
                variant="outlined"
                onChange={(e) => setnumberOfRoom(e.target.value)}
                required
                value={numberOfRoom}
              />
            </div>
          </div>
          <div className=" border-2 my-4 ">
            <div className="mb-4 mt-4">
              <h2 className="text-white ml-2 mt-2 mb-2">
                What kind of beds are available in this room?
              </h2>
              <TextField
                style={{ width: "500px", marginLeft: "10px" }}
                size="small"
                id="outlined-basic"
                label="Enter Available Beds"
                variant="outlined"
                onChange={(e) => setavailableBeds(e.target.value)}
                required
                value={availableBeds}
              />
              <TextField
                style={{ width: "300px", marginLeft: "10px" }}
                size="small"
                id="outlined-basic"
                label="Enter Number of Beds"
                variant="outlined"
                onChange={(e) => setnumberOfBeds(e.target.value)}
                required
                value={numberOfBeds}
              />
            </div>
            <div className="ml-2 cursor-pointer mb-4 flex">
              <div>
                <AddCircleOutlinedIcon />
              </div>
              <div className="translate-y-0.5">Add another bed</div>
            </div>
            <div className="mb-4 mt-4">
              <h2 className="text-white ml-2 mt-2 mb-2">
                How many guests can stay in the room?
              </h2>
              <div className="flex">
                <div>
                  <TextField
                    style={{ width: "500px", marginLeft: "10px" }}
                    size="small"
                    id="outlined-basic"
                    label="Enter Number of Guests"
                    variant="outlined"
                    onChange={(e) => setnumberOfGuest(e.target.value)}
                    required
                    value={numberOfGuest}
                  />
                </div>
                <div className=" translate-x-4 translate-y-0.5">
                  <PersonOutlineOutlinedIcon />
                </div>
              </div>
            </div>
          </div>
          <div className=" border-2 my-4 ">
            <div className="mb-4 mt-4">
              <h2 className="text-white ml-2 mt-2 mb-2">Bese price per night</h2>
              <span className="ml-4">Price for one person</span>
              <div>
                <TextField
                  style={{ width: "600px", marginLeft: "10px" }}
                  size="small"
                  id="outlined-basic"
                  label="Enter Price"
                  variant="outlined"
                  onChange={(e) => setpriceOfOnePerson(e.target.value)}
                  required
                  value={priceOfOnePerson}
                />
              </div>
              <center>
                <input
                  type={"submit"}
                  value={"Save Details"}
                  style={{ color: "white" }}
                />
              </center>
            </div>
          </div>
        </form>
        <div className=" text-center">
          <Button
            style={{ padding: "10px 300px 10px 300px" }}
            variant="contained"
            onClick={() =>
              history(
                `/pm-dashboard/${localStorage.getItem("username")}/property`
              )
            }
          >
            Continue
          </Button>
        </div>
        <br />
      </div>
    );
  };
  
  export default editPack;
