import React,{Component} from "react";
import axios from "axios";
import {Link} from 'react-router-dom';

import   '../App.cs';
//import Package from "../../../TourPackage/BACKEND/models/Package";



export default class AllFeedbacks extends Component{


   constructor(props){

       super(props);

       this.state={

        feedbacks:[]
       };

   }
  componentDidMount(){

   this.retriveFeedbacks();
  }
  retriveFeedbacks(){

   axios.get("http://localhost:8070/feedback/allFB").then(res=>{

      if(res.data.success){
          this.setState({

            feedbacks:res.data.existingFeedbacks

          });

          console.log(this.state.feedbacks)

      }


   })



  }



  
     
  onDelete(id){

     fetch(`http://localhost:8070/feedback/delete/${id}`,{

           method:`DELETE`




     }).then((result)=>{

         result.json().then((resp)=>{

           console.warn(resp)
           alert("Feedback Deleted")
           this.retriveFeedbacks()

         })


     }) 
    


  }



 render(){

    return(

      <div className="container" class="p-3 mb-2 bg-secondary text-white">

      <h1 className="primary"><center><strong>All Feedbacks</strong></center></h1>
      <table class="table table-dark table-hover">
      <thead>
      <tr class="text-danger">
           <th scope="col">Count</th>
           <th scope="col">User Name</th>
           <th scope="col">Selected Package ID</th>
           <th scope="col">suggesions</th>
           <th scope="col">Faults and Errors</th>
           <th scope="col">Rating</th>
           <th scope="col">Date</th>
           <th scope="col">Action</th>
        
  
      </tr>
     </thead>
     <tbody>

       {this.state.feedbacks.map((feedbacks,index)=>(

        <tr>
          <th scope="row">{index+1}</th>
          <td>{feedbacks.Uname}</td>
          <td>{feedbacks.pId}</td>
          <td>{feedbacks.suggess}</td>
          <td>{feedbacks.fande}</td>
          <td>{feedbacks.rate}</td>
          <td>{feedbacks.date}</td>
       
         <td> <button type="button" class="btn btn-success" onClick={()=>this.onDelete(feedbacks._id)}><i className="fa-solid fa-trash-can "></i>&nbsp;&nbsp;Remove Feedback</button></td>
         

        </tr>

       ))}



     </tbody>



     </table>
     <a href="man" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Admin's page</strong></a>
      </div>

    

    ) 
 }

 }
