import React,{Component} from "react";
import axios from "axios";
import Train from '../images/trav.jpg'
import '../App.css';
//import Package from ".../src/Package";
//import Package from "../../../TourPackage/BACKEND/models/Package";



export default class cusPack extends Component{


   constructor(props){

       super(props);

       this.state={

        packages:[]
       };

   }
  componentDidMount(){

   this.retrivePackages();
  }
  retrivePackages(){

   axios.get("http://localhost:8070/package/all").then(res=>{

      if(res.data.success){
          this.setState({

            packages:res.data.existingPackages

          });

          console.log(this.state.packages)

      }


   })



  }

 //Filter /Search Mechod
 filterContent(packages,searchTerm){

    const results=packages.filter((packages)=>packages.destination.toLowerCase().includes(searchTerm));
    this.setState({packages:results});

 }


  
  handleTextSearch=(e)=>{

     const searchTerm=e.currentTarget.value;
     axios.get("http://localhost:8070/package/all").then(res=>{

      if(res.data.success){
         this.filterContent(res.data.existingPackages,searchTerm)
      }
  });

}; 


  

 render(){

   

    return(

     //<i class="fa-solid fa-plane-departure"></i>

      <div class="p-3 mb-2 bg-dark text-white">
      <div className="container">  
      
      <div class="text-center"><img class="img-fluid" src={Train} height={100} width={400}  alt="Card image cap" /></div>
      <a className="customize" href="/cuz"  class="btn btn-success" style={{position:"absolute",right:150,top:350,}}><strong><i className="fa-solid fa-plane-departure"></i>&nbsp;&nbsp;Customize Your Tour</strong></a>&nbsp;&nbsp;
      <div className="row">
       <div className="col-lg-9 mt-2 mb-2">
          <h1 className="Ball"><strong>Search your Destination </strong>-</h1>
          

       </div>
       
       <div className="col-lg-3 mt-2 mb-2" class="text-center">
          
        
             <input
              className="form-control"
              type="search"
              placeholder="Search"
              name="searchTerm"
              onChange={this.handleTextSearch}
             
             ></input>


       </div>
     
       
     </div>

     <br></br>
      <table class="table table-striped table-dark">
      <thead>
      <tr>
           <th scope="col">Count</th>
           <th scope="col">Package Name</th>
           <th scope="col">Package ID</th>
           <th scope="col">Destination</th>
           <th scope="col">NumberofDays</th>
           <th scope="col">NumberofPassengers</th>
           <th scope="col">Hotel</th>
           <th scope="col">Transport</th>
           <th scope="col">TourGuide</th>
           <th scope="col">TotalPrice(Rs)</th>
          
  
  
      </tr>
     </thead>
     <tbody>

       {this.state.packages.map((packages,index)=>(

        <tr>
          <th scope="row">{index+1}</th>
          <td class="bg-danger">{packages.name}</td>
          <td>{packages.packId}</td>
          <td class="bg-danger">{packages.destination}</td>
          <td>{packages.numofdays}</td>
          <td class="bg-danger">{packages.nopass}</td>
          <td>{packages.hotel}</td>
          <td class="bg-danger">{packages.transport}</td>
          <td>{packages.tourGuide}</td>
          <td class="bg-danger">{packages.totPrice}</td>

         
    


        </tr>
        

       ))}



     </tbody>



     </table>

      




     <a href="/front" class="btn btn-warning"><strong>Back to home</strong></a>&nbsp;&nbsp;
      <a href="/fb" class="btn btn-warning"><strong>Give feedbacks</strong></a>
      

      </div>

    </div>

    ) 
 }

 }
