import React,{useEffect, useState} from "react"
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";



export default function EditCusPackz(){

    const [name,setName] = useState("");
    const [email,setMail] = useState("");
    const [phone,setPhone] = useState("");
    const [country,setCountry] = useState("");
    const [arriDate,setDate] = useState("");
    const [pickPlace,setPlace] = useState("");
    const [numofAdd,setAddu] = useState("");
    const [numOfChild,setChild] = useState("");
    const [duration,setDurat] = useState("");
    const [notes,setNotes] = useState("");

  const { id } = useParams();

  useEffect(() => {
      getCusPacks();
    }, []);
  
    //let navigate= useNavigate();
  
    function getCusPacks() {
      let cusPack = true;
  
      fetch(`http://localhost:8070/cusPack/get/${id}`)
        .then((res) => res.json())
  
        .then((result) => {
          if (cusPack) {
            setName(result.name);
            setMail(result.email);
            setPhone(result.phone);
            setCountry(result.country);
            setDate(result.arriDate);
            setPlace(result.pickPlace);
            setAddu(result.numofAdd);
            setChild(result.numOfChild);
            setDurat(result.duration);
            setNotes(result.notes);
           
          }
          console.log(result);
        });
  
      return () => (cusPack = false);
    }

  
  function updateData(e){

      e.preventDefault();

      //alert("Insert");

      const updateCusPacks ={
        name,
       email,
       phone,
       country,
       arriDate,
       pickPlace,
       numofAdd,
       numOfChild, 
       duration,
       notes
      }

      axios
    .patch(`http://localhost:8070/cusPack/update/${id}`, updateCusPacks)
    .then((_res) => {
      alert("Your Package Updated Successfully!");
     // navigate("/front");
      console.log(updateCusPacks);
    })
    .catch((_err) => {
      
      alert("Database Error");
    });
  }



return(
    <div className="container">
        <h2>Update User Package Details</h2>
        <form className =  "CusPackages">
        <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputEmail4">User Name</label>
                    <input type="text" className="form-control" id="uname" placeholder="User Name" value={Uname} onChange={(e)=>{setName(e.target.value);}}/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="iinputPid">Package ID</label>
                    <input type="text" className="form-control" id="Pid" placeholder="Package ID" value={pId} onChange={(e)=>{setMail(e.target.value);} }/>
                </div>
            </div>

            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputSugges">Suggestions</label>
                    <input type="text" className="form-control" id="suggest" placeholder="Enter any suggestions" value={suggess} onChange={(e)=>{setPhone(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputFande">Faliors and Errors</label>
                    <input type="text" className="form-control" id="fande" placeholder="Faliors and Errors" value={fande} onChange={(e)=>{setCountry(e.target.value);} }/>
                </div>
            </div>

          
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Rate US</label>
                    <input type="text" className="form-control" id="rate" placeholder="Enter your Rate" value={rate} onChange={(e)=>{setDate(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Date</label>
                    <input type="text" className="form-control" id="date" placeholder="DD/MM/YY" value={date}  onChange={(e)=>{setPlace(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Rate US</label>
                    <input type="text" className="form-control" id="rate" placeholder="Enter your Rate" value={rate} onChange={(e)=>{setAddu(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Date</label>
                    <input type="text" className="form-control" id="date" placeholder="DD/MM/YY" value={date}  onChange={(e)=>{setChild(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Rate US</label>
                    <input type="text" className="form-control" id="rate" placeholder="Enter your Rate" value={rate} onChange={(e)=>{setDurat(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Date</label>
                    <input type="text" className="form-control" id="date" placeholder="DD/MM/YY" value={date}  onChange={(e)=>{setNotes(e.target.value);} }/>
                </div>
            </div>

        
            
        </form><br></br><div>
            
                <button  type="button" class="btn btn-success"   onClick={updateData}> <i className="fa-solid fa-wrench "></i>&nbsp;&nbsp; Update Feedback Details&nbsp;&nbsp;&nbsp;</button>&nbsp;&nbsp;
            <a href="/cfb" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Admin's page</strong></a>
            </div>
            
            
    </div>
)

}
