import React,{Component} from "react";
import axios from "axios";
import TableRow from './tableRaw';


export default class AllPacks extends Component{


   constructor(props){

       super(props);

       this.state={

        packages:[]
       };

   }
  componentDidMount(){

   this.retrivePackages();
  }
  retrivePackages(){

   axios.get("http://localhost:8070/package/all").then(res=>{

      if(res.data.success){
          this.setState({

            packages:res.data.existingPackages

          });

          console.log(this.state.packages)

      }


   })



  }


  TabRow(){
   return this.state.packages.map(function (object,i){
        return <TableRow obj={object} key={i}></TableRow>;
  

   });


 } 





 render(){

    return(

      <div>

      <h3 aligh="center">Tour Package List</h3>
      <table className="table table-striped" style={{marginTop:20}}>
      <thead>
      <tr>
           <th>Package Name</th>
           <th>Package ID</th>
           <th>Destination</th>
           <th>NumberofDays</th>
           <th>NumberofPassengers</th>
           <th>Hotel</th>
           <th>Transport</th>
           <th>TourGuide</th>
           <th>TotalPrice(Rs)</th>
           <th colSpan="1">Action</th>
  
  
      </tr>
     </thead>
     <tbody>
     {this.TabRow()}
  
  
  
     </tbody>
     </table>
      </div>



    )





 }





}
