//import React, {useState, useEffect, Component} from 'react';
import React,  {Component} from 'react';
import axios from "axios";
import TableRow from './TableRow';


export default class AllPackages extends Component{


   constructor(props){

    super(props);
    this.state={packages : []};


   }
  componentDidMount()
{

  axois.get('http://localhost:8070/package/').then(response=>{

       this.setState({packages:response.data});


  }).catch(function(error){

     console.log(error);

  })

}

 TabRow(){
   return this.state.packages.map(function (object,i){
        return <TableRow obj={object} key={i}></TableRow>;
  

   });


 } 



render(){

  return(
    <div>

    <h3 aligh="center">Business List</h3>
    <table className="table table-striped" style={{marginTop:20}}>
    <thead>
    <tr>
         <th>Package Name</th>
         <th>Package ID</th>
         <th>Destination</th>
         <th>Number of Days</th>
         <th>Number of Passengers</th>
         <th>Hotel</th>
         <th>Transport</th>
         <th>Tour Guide</th>
         <th>Total Price(Rs)</th>
         <th colSpan="2">Action</th>


    </tr>
   </thead>
   <tbody>
   {this.tabRow()}



   </tbody>
   </table>
    </div>


  );
  
    

  

 

}


}



















