import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';

//Retrive Vehile details
export default class packReport extends Component {


    constructor(props) {

        super(props);

        this.state = {

            vehicles: []
        };

    }
    componentDidMount() {

        this.retriveVehicles();
    }
    retriveVehicles() {

        axios.get("http://localhost:8070/vehicle/all").then(res => {

            if (res.data.success) {
                this.setState({

                    vehicles: res.data.existingVehicles

                });

                console.log(this.state.vehicles)

            }


        })



    }
   
 //Report Generation Part
    repotGen=()=>{

         window.print();

    }



    render() {

        return (

            <div className="container" class="p-3 mb-2 bg-secondary text-white">

                <h1><center><strong>All Registered Vehicle Details</strong></center></h1>
                <table class="table table-dark table-hover">
                    <thead>
                        <tr class="text-danger">
                        <th scope="col">Index</th>
                        <th scope="col">Owner's Name</th>
                        <th scope="col">NIC Number</th>
                        <th scope="col">Vehicle Category</th>
                        <th scope="col">Vehicle Reg Number</th>
                        <th scope="col">Address</th>
                        <th scope="col">Mobile Number</th>
                           


                        </tr>
                    </thead>
                    <tbody>

                        {this.state.vehicles.map((vehicles, index) => (

                            <tr>
                            <th scope="row">{index + 1}</th>
                           <td>{vehicles.name}</td>
                           <td>{vehicles.nic}</td>
                           <td class="bg-danger">{vehicles.vehiCateg}</td>
                           <td>{vehicles.vehiNum}</td>
                           <td class="bg-danger">{vehicles.address}</td>
                           <td>{vehicles.phone}</td>

                            </tr>

                        ))}



                    </tbody>

            

                </table>

             <a href="/man" class="btn btn-dark"><strong>Home</strong></a>&nbsp;&nbsp;    

            <button onClick={this.repotGen}>Print</button>
            

            </div>



        )
    }

}
