import React,{useState,useEffect} from 'react';
import{useHistory,useLocation} from "react-router-dom";
import axios from 'axios';
//import AddPackage from './addPackage';
import {toast} from "react-toastify";


   const initialState={

    name:"",
    packId:"",
    destination:"",
    numofdays:"",
    nopass:"",
    hotel:"",
    transport:"",
    tourGuide:"",
    totPrice:"",


   };


const AddPack =()=>{
   
    const[state, setState]=useState(initialState);
    const{name,packId,destination,numofdays,nopass,hotel,transport,tourGuide,totPrice}=state;
     
    const history=useHistory();
    const AddPackage=async(data) =>{
       
       const response = await axios.post('http://localhost:8070/package/add',data);
            if(response.status===200){
            toast.success(response.data);


            }
    };


    const handleSubmit=(e) =>{

      e.preventDefault();
      if(!name || !packId || !destination || !numofdays || !nopass || !hotel || !transport || !tourGuide || !totPrice){

        toast.error("Please provide value into each input field");

      }else{
       AddPackage(state);
       history.push("/")
   

      }
       


    }

    const handleInputChange = (e) => {

       let{name,value}=e.target;
       setState({...state,[name]:value});

    };





    return(

    
     <div style={{marginTop:"100px"}}>
         <form>
         <div className="form-group" style={{marginBottom:"15px"}}>

            onSubmit={handleSubmit}
      
            <label htmlFor="name">Package Name</label>
            <input>
                type="text",
                id="name",
                name="name",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={name}
            
            
            </input>
            

            <label htmlFor="packId">Package Id</label>
            <input>
                type="text",
                id="packId",
                name="packId",
                placeholder="Enter Package ID",
                onChange={handleInputChange}
                value={packId}
            
            
            </input>



            <label htmlFor="destination">Destination</label>
            <input>
                type="text",
                id="destination",
                name="destination",
                placeholder="Enter Destination",
                onChange={handleInputChange}
                value={destination}
            
            
            </input>


            <label htmlFor="numofdays">Number Of Days</label>
            <input>
                type="text",
                id="numofdays",
                name="numofdays",
                placeholder="Enter Number Of Days",
                onChange={handleInputChange}
                value={numofdays}
            
            
            </input>

            <label htmlFor="nopass">Number Of Passengers</label>
            <input>
                type="text",
                id="nopass",
                name="nopass",
                placeholder="Enter Number Of Passengers",
                onChange={handleInputChange}
                value={nopass}
            
            
            </input>

            <label htmlFor="hotel">Hotel</label>
            <input>
                type="text",
                id="hotel",
                name="hotel",
                placeholder="Enter Hotel Name",
                onChange={handleInputChange}
                value={hotel}
            
            
            </input>

            <label htmlFor="transport">Transport</label>
            <input>
                type="text",
                id="transport",
                name="transport",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={transport}
            
            
            </input>

            <label htmlFor="tourGuide">Tour Guide</label>
            <input>
                type="text",
                id="tourGuide",
                name="tourGuide",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={tourGuide}
            
            
            </input>

            <label htmlFor="totPrice">Total Price</label>
            <input>
                type="text",
                id="totPrice",
                name="totPrice",
                placeholder="Enter Package Name",
                onChange={handleInputChange}
                value={totPrice}
            
              
            </input>

            <input type="submit" value="Add"/>
            




    </div>

         </form>
        
     </div>


    );




};
export default AddPack;
