import React,{useState} from "react";
//import React,{Component} from "react"
import axios from "axios";

//import axios from "axios";
//import {setErrors} from "./../components/setErrors";
 







function AddPackage(){

  const [name,setName] = useState("");
  const [packId,setPid] = useState("");
  const [destination,setDesti] = useState("");
  const [numofdays,setDays] = useState("");
  const [nopass,setPass] = useState("");
  const [hotel,setHotel] = useState("");
  const [transport,setTrans] = useState("");
  const [tourGuide,setGuide] = useState("");
  const [totPrice,setPrice] = useState("");


  // (local function) sendData(e: any):void
  function sendData(e){
   e.preventDefault();
   
   const newPackage={

     name,
     packId,
     destination,
     numofdays,
     nopass,
     hotel,
     transport,
     tourGuide,
     totPrice


   }
   axios.post("http://localhost:8070/package/add",newPackage).then(()=>{

     alert("Package Added")
   }).catch((err)=>{

     alert(err)

   })

  }

 return(
   

   <div className="container" class="p-3 mb-2 bg-secondary text-dark">
    
    <form onSubmit={sendData} style={{position: 'absolute' ,right:300,}}>
    <h1>Please Enter New Tour Package Details</h1><br></br>
    
    <div className="form-group" style={{width:"900px"}}>
      <label for="Name"><strong>Package Name</strong></label>
      <input type="text" class="form-control" id="pacName" placeholder="Enter Package Name here"
      onChange={(e)=> {

        setName(e.target.value);

       } }/>
  
    </div><br></br>
    
    <div className="form-group" style={{width:"900px"}}>
      <label for="id"><strong>Package ID</strong></label>
      <input type="text" class="form-control" id="packId" placeholder="Enter Package ID here"
       onChange={(e)=> {

        setPid(e.target.value);

       } }/>
      
      
  
    </div><br></br>
  
    <div className="form-group" style={{width:"900px"}}>
      <label for="desti"><strong>Destination</strong></label>
      <input type="text" class="form-control" id="desti" placeholder="Enter Your Destination here"
      onChange={(e)=> {

        setDesti(e.target.value);

       } }/>
      
  
    </div><br></br>

    <div className="form-group" style={{width:"900px"}}>
      <label for="numDays"><strong>Number Of Days</strong></label>
      <input type="text" class="form-control" id="numDays" placeholder="Enter Your Number Of Days"
      onChange={(e)=> {

        setDays(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"900px"}}>
      <label for="numPass"><strong>Number Of Passengers</strong></label>
      <input type="text" class="form-control" id="numPass" placeholder="Enter Your Number Of Passengers"
      onChange={(e)=> {

        setPass(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"900px"}}>
      <label for="hotel"><strong>Hotel</strong></label>
      <input type="text" class="form-control" id="hotel" placeholder="Enter Hotel Name"
      onChange={(e)=> {

        setHotel(e.target.value);

       } }/>

    
    </div><br></br>


    <div className="form-group" style={{width:"900px"}}>
      <label for="transport"><strong>Transport</strong></label>
      <input type="text" class="form-control" id="transport" placeholder="Enter Transport Company"
      onChange={(e)=> {

        setTrans(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"900px"}}>
      <label for="guide"><strong>Tour Guide</strong></label>
      <input type="text" class="form-control" id="guide" placeholder="If Tour Guide With/Without?"
      onChange={(e)=> {

        setGuide(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"900px"}}>
      <label for="price"><strong>Total Price</strong></label>
      <input type="text" class="form-control" id="price" placeholder="Enter Total Price"
      onChange={(e)=> {

        setPrice(e.target.value);

       } }/>

    
    </div><br></br>
    <a href="man" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Admin's page</strong></a>&nbsp;
    <button type="submit" class="btn btn-danger" style={{width:"300px"}}><strong><i className="fa-solid fa-folder-plus"></i>&nbsp;&nbsp;Create package</strong></button>&nbsp;
    <a href="/newPack" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-chevron-right"></i>&nbsp;&nbsp;Get new package</strong></a>&nbsp;
   
    
   </form>
   </div>

  


 )




} 

export default AddPackage
