import React,{useState} from 'react';
import{useHistory} from "react-router-dom";
import axios from 'axios';
import AddPackage from './addPackage';
import {toast} from "react-toastify";


   const initialState={

    name:"",
    packId:"",
    destination:"",
    numofdays:"",
    nopass:"",
    hotel:"",
    transport:"",
    tourGuide:"",
    totPrice:"",


   };


const AddPack =()=>{
   
    const[state, setState]=useState(initialState);
    const{name,packId,destination,numofdays,nopass,hotel,transport,tourGuide,totPrice}=state;
     
    const history=useHistory();
    const AddPackage=async(data) =>{
       
       const response = await axios.post('http://localhost:8070/package/add',data);
            if(response.status===200){
            toast.success(response.data);


            }
    };


    const handleSubmit=(e) =>{

      e.preventDefault();
      if(!name || !packId || !destination || !numofdays || !nopass || !hotel || !transport || !tourGuide || !totPrice){

        toast.error("Please provide value into each input field");

      }else{
       AddPackage(state);
       history.push("/")
   

      }
       


    }

    const handleInputChange = (e) => {

       let{name,value}=e.target;
       setState({...state,[name]:value});

    };


    

     return(
     
     <div className="col-md-8 mt-4 mx-auto">
       <h1 className="h3 mb-3 font-weight-normal">Create New Package</h1>
       <form className="needs-validation" noValidate>
       onSubmit={handleSubmit}
         <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Package Name</lable>
           <input type="text"
           className="form-control"
           name="packageName"
           placeholder="Enter Package Name"
           Defaultvalue={this.state.name}
           onChange={this.handleInputChange}/>
          
       </div>    


       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Package ID</lable>
           <input type="text"
           className="form-control"
           name="packageID"
           placeholder="Enter Package ID"
           Defaultvalue={this.state.packId}
           onChange={this.handleInputChange}/>
          
       </div>    


       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Destination</lable>
           <input type="text"
           className="form-control"
           name="Desti"
           placeholder="Enter Destination"
           Defaultvalue={this.state.destination}
           onChange={this.handleInputChange}/>
          
       </div>    


       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Number Of Days</lable>
           <input type="text"
           className="form-control"
           name="NumOfDays"
           placeholder="Enter Number of Days"
           Defaultvalue={this.state.numofdays}
           onChange={this.handleInputChange}/>
          
       </div>  



       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Number Of Passengers</lable>
           <input type="text"
           className="form-control"
           name="numOfPass"
           placeholder="Enter Number Of Passengers"
           Defaultvalue={this.state.nopass}
           onChange={this.handleInputChange}/>
          
       </div>  



       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Hotel</lable>
           <input type="text"
           className="form-control"
           name="Hotel"
           placeholder="Enter Hotel Name"
           Defaultvalue={this.state.hotel}
           onChange={this.handleInputChange}/>
          
       </div>  
       

       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Transport</lable>
           <input type="text"
           className="form-control"
           name="Transport"
           placeholder="Enter Transport Option"
           Defaultvalue={this.state.transport}
           onChange={this.handleInputChange}/>
          
       </div>  


       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Tour Guide(With/without)</lable>
           <input type="text"
           className="form-control"
           name="Guide"
           placeholder="With/Without"
           Defaultvalue={this.state.tourGuide}
           onChange={this.handleInputChange}/>
          
       </div>  
       

       <div className="form-group" style={{marginBottom:"15px"}}>
           <lable style={{marginBottom:'5px'}}>Total Price</lable>
           <input type="text"
           className="form-control"
           name="totPrice"
           placeholder="Enter Total Price"
           Defaultvalue={this.state.totPrice}
           onChange={this.handleInputChange}/>
          
       </div>  

       <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
          <i className="far fa-check-square"></i>
           &nbsp;Submit
       </button>
       

     </form>
     </div>
    

     )



     




     };





