 import React, { useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import {

  useParams,
} from "react-router-dom";
import AllPacks from './AllPacks';

const EditPackage = () => {
  const params = useParams();
  const [packageDetails, setPackageDetails] = React.useState({});
  const [updatedPackageDetails, setUpdatedPackageDetails] = React.useState({});

  useEffect(() => {
    fetchPackage();
  }, [])

  const fetchPackage = async () => {
    const response = await fetch(`http://localhost:8060/package/get/${params.id}`);
    const data = await response.json();
    setPackageDetails(data.packages);
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedPackageDetails({ ...updatedPackageDetails, [name]: value });
  }

  const updatePackage = (e) => {
    e.preventDefault();
    console.log(updatedPackageDetails);
    axios.put(`http://localhost:8060/package/update/${params.id}`, updatedPackageDetails).then((res) => {
      if (res.data.success) {
        alert("package update succesfully");
        this.retrivePackages();
      }
    })
  }


  return (
    <div className="container">

      



      <form className="needs-validation" noValidate>
       
        <div class="form group">
          <label for="package_type" class="form-label">
            Package Type
          </label>
          <input
            type="text"
            class="form-control"
            name="package_type"
            id="package_type"
            placeholder="Enter Package Type"
            defaultValue={packageDetails.name}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="package_name" class="form-label">
            Package ID
          </label>
          <input
            type="text"
            class="form-control"
            id="package_ID"
            name='package_ID'
            placeholder="Enter Package ID"
            defaultValue={packageDetails.packID}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="day_range" class="form-label">
            Destination
          </label>
          <input
            type="text"
            class="form-control"
            id="desti"
            name='desti'
            placeholder="Enter Destination"
            defaultValue={packageDetails.destination}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="price_range" class="form-label">
           Num Of days
          </label>
          <input
            type="text"
            class="form-control"
            id="numOfDays"
            name='numOfDays'
            placeholder="Enter Number Of Days"
            defaultValue={packageDetails.numofdays}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Num of Passengers
          </label>
          <input
            type="text"
            class="form-control"
            id="noPass"
            name='noPass'
            placeholder="Enter Num of Passengers"
            defaultValue={packageDetails.nopass}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Hotel
          </label>
          <input
            type="text"
            class="form-control"
            id="hotel"
            name='hotel'
            placeholder="Enter Hotel Name"
            defaultValue={packageDetails.hotel}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Transport
          </label>
          <input
            type="text"
            class="form-control"
            id="trans"
            name='trans'
            placeholder="Enter Num of Transport"
            defaultValue={packageDetails.transport}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Tour Guide
          </label>
          <input
            type="text"
            class="form-control"
            id="guide"
            name='guide'
            placeholder="Enter Tour Guide With Or Without"
            defaultValue={packageDetails.tourGuide}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            total
          </label>
          <input
            type="text"
            class="form-control"
            id="tot"
            name='tot'
            placeholder="Enter Total Price"
            defaultValue={packageDetails.totPrice}
            onChange={handleInputChange}
          />
        </div>
        
        <button type="submit" class="btn btn-success" style={{ marginTop: '16px' }}   onClick={updatePackage}> <i className="far fa-check-square"></i>  &nbsp;update  </button>
        
      </form>
    </div>
  );
}

export default EditPackage;
