//import React from "react";
import React,{Component} from "react";
//import React, {Component} from "react";
import axios from 'axios';
import AllPacks from './../components/AllPacks'
//import '../App.css';
//import "bootstrap/dist/css/bootstrap.min.css";


 export default class editPack extends Component{

  constructor(props){
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePackId = this.onChangePackId.bind(this);
    this.onChangeDestination = this.onChangeDestination.bind(this);
    this.onChangeNumOfDays = this.onChangeNumOfDays.bind(this)
    this.onChangeNumOfPass = this.onChangeNumOfPass.bind(this)
    this.onChangeHotel = this.onChangeHotel.bind(this)
    this.onChangeTransp = this.onChangeTransp.bind(this)
    this.onChangeGuide = this.onChangeGuide.bind(this)
    this.onChangeTot = this.onChangeTot.bind(this)
    this.onSubmit = this.onSubmit.bind(this);
  
    this.state = {
        packages:[],
        
         name:'',
        packId:'',
       destination:'',
        numofdays:'',
        nopass:'',
        hotel:'',
        transport:'',
        tourGuide:'',
        totPrice:''  
            
    }
  } 

  componentDidMount() {
    this.retrivePackages();
  }

  retrivePackages(){
    axios.get("http://localhost:8070/package/get/62635d88c4fa13160079182b")
      .then(response => {
        console.log(response.data);
        //this.setState({packages:response.data.Package})
        this.setState({packages:response.data.package});
        this.setState({
      
              name:response.data.name,
              packId:response.data.packId,
              destination:response.data.destination,
              numofdays:response.data.numofdays,
              nopass:response.data.nopass,
              hotel:response.data.hotel,
              transport:response.data.transport,
              tourGuide:response.data.tourGuide,
              totPrice:response.data.totPrice


        });
      })
      .catch(function (error){
        console.log(error);
    })
  }
  

  onChangeName(e){
    this.setState({
      name: e.target.value
    });
  }

  onChangePackId(e){
    this.setState({
      packId: e.target.value
    });
  }

  onChangeDestination(e){
    this.setState({
      destination: e.target.value
    });
  }

  onChangeNumOfDays(e){
    this.setState({
      numofdays: e.target.value
    });
  }

  onChangeNumOfPass(e){
    this.setState({
      nopass: e.target.value
    });
  }

  onChangeHotel(e){
    this.setState({
      hotel: e.target.value
    });
  }

  onChangeTransp(e){
    this.setState({
      transport: e.target.value
    });
  }

  onChangeGuide(e){
    this.setState({
      tourGuide: e.target.value
    });
  }

  onChangeTot(e){
    this.setState({
      totPrice : e.target.value
    });
  } 
   
  onSubmit(e){
    e.preventDefault();
      const obj = {
      
      name: this.state.name,
      packId: this.state.packId,
      destination: this.state.destination,
      numofdays : this.state.numofdays ,
      nopass: this.state.nopass,
      hotel: this.state.hotel,
      transport: this.state.transport,
      tourGuide: this.state.tourGuide,
      totPrice : this.state.totPrice 
     
    }

    axios.put("http://localhost:8070/package/update/62635d88c4fa13160079182b",obj ).then(()=>{
      alert("Package Successfully Updated!");
    }).catch((err)=>{
        alert(err)
    })
  }


 render() {
  return (
    
    <div className="container" class="p-3 mb-2 bg-secondary text-white">
    <form>
    <h1>Enter update details here</h1><br></br>
    <div className="form-group" style={{width:"700px"}}>
      <label for="Name"><strong>Package Name - </strong></label>
      <input type="text" class="form-control" id="pacName" placeholder="Enter Package Name here" defaultValue={this.state.packages.name}
       onChange={this.onChangeName} />
  
    </div>
    
    <div className="form-group" style={{width:"700px"}}>
      <label for="id"><strong>Package ID - </strong></label>
      <input type="text" class="form-control" id="packId" placeholder="Enter Package ID here" defaultValue={this.state.packages.packId}
       onChange={this.onChangePackId} />
      
      
  
    </div>
  
    <div className="form-group" style={{width:"700px"}}>
      <label for="desti"><strong>Destination - </strong></label>
      <input type="text" class="form-control" id="desti" placeholder="Enter Your Destination here" defaultValue={this.state.packages.destination}
       onChange={this.onChangeDestination} />
      
  
