    }
  }, [id]);

  const getSingleUser = async () => {
    const response = await fetch(`http://localhost:8070/package/get/${params.id}`);
    const data = await response.json();
    setPackageDetails(data.package);
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedPackageDetails({ ...updatedPackageDetails, [name]: value });
  }

  const updatePackage = (e) => {
    e.preventDefault();
    console.log(updatedPackageDetails);
    axios.put(`http://localhost:8070/package/update/${params.id}`, updatedPackageDetails).then((res) => {
      if (res.data.success) {
        alert("package update succesfully");
        this.retrivePackages();
      }
    })
  }


  return (
    

       
                    
          



      <form className="needs-validation" noValidate>
             <div class="form group">
          <label for="package_Name" class="form-label">
            Package Name
          </label>
          <input
            type="text"
            class="form-control"
            name="packname"
            id="packname"
            placeholder="Enter Package Name"
            defaultValue={packageDetails.name}
            onChange={handleInputChange}
          />
        </div>


        <div class="form group">
          <label for="package_type" class="form-label">
            Package ID
          </label>
          <input
            type="text"
            class="form-control"
            name="packID"
            id="packID"
            placeholder="Enter Package ID"
            defaultValue={packageDetails.packId}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="package_Destination" class="form-label">
            Destination
          </label>
          <input
            type="text"
            class="form-control"
            id="desti"
            name='desti'
            placeholder="Enter Destination"
            defaultValue={packageDetails.destination}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="day_range" class="form-label">
            Number of days
          </label>
          <input
            type="text"
            class="form-control"
            id="numOFDays"
            name='numOFDays'
            placeholder="Enter number of days"
            defaultValue={packageDetails.numofdays}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="price_range" class="form-label">
            Price Range
          </label>
          <input
            type="text"
            class="form-control"
            id="NumOFPSs"
            name='NumOFPSs'
            placeholder="Enter number of passengers"
            defaultValue={packageDetails.nopass}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Other Details
          </label>
          <input
            type="text"
            class="form-control"
            id="Hotel"
            name='Hotel'
            placeholder="Enter hotel name"
            defaultValue={packageDetails.hotel}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Other Details
          </label>
          <input
            type="text"
            class="form-control"
            id="trans"
            name='trans'
            placeholder="Enter transport servise name"
            defaultValue={packageDetails.transport}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Other Details
          </label>
          <input
            type="text"
            class="form-control"
            id="guide"
            name='guide'
            placeholder="Enter tour guide name"
            defaultValue={packageDetails.tourGuide}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="other_details" class="form-label">
            Other Details
          </label>
          <input
            type="text"
            class="form-control"
            id="tot"
            name='tot'
            placeholder="Enter total price"
            defaultValue={packageDetails.totPrice}
            onChange={handleInputChange}
          />
        </div>

      


        
        <button type="submit" class="btn btn-success"   onClick={updatePackage}> <i className="far fa-check-square"></i>  &nbsp;update  </button>
        
      </form>
  
  );
}

export default EditPackage
