//import React from "react";
import React,{Component} from "react";
//import React, {Component} from "react";
import axios from 'axios';
//import '../App.css';
//import "bootstrap/dist/css/bootstrap.min.css";


 export default class editPack extends Component{

  constructor(props){
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePackId = this.onChangePackId.bind(this);
    this.onChangeDestination = this.onChangeDestination.bind(this);
    this.onChangeNumOfDays = this.onChangeNumOfDays.bind(this)
    this.onChangeNumOfPass = this.onChangeNumOfPass.bind(this)
    this.onChangeHotel = this.onChangeHotel.bind(this)
    this.onChangeTransp = this.onChangeTransp.bind(this)
    this.onChangeGuide = this.onChangeGuide.bind(this)
    this.onChangeTot = this.onChangeTot.bind(this)
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      packages:[]
      // card_num: '',
      // full_name: '',
      // cvv: '',
      // expiry_date: '',
      // street_address: '',
      // country: '',
      // city: '',
      // zip_code: '',
      // state: ''
    }
  }

  componentDidMount() {
    this.retrivePackages();
  }

  retrivePackages(){
    axios.get("http://localhost:8070/package/all")
      .then(response => {
        console.log(response.data);
        this.setState({packages:response.data.Package})
      })
      .catch(function (error){
        console.log(error);
    })
  }

  onChangeName(e){
    this.setState({
      name: e.target.value
    });
  }

  onChangePackId(e){
    this.setState({
      packId: e.target.value
    });
  }

  onChangeDestination(e){
    this.setState({
      destination: e.target.value
    });
  }

  onChangeNumOfDays(e){
    this.setState({
      numofdays: e.target.value
    });
  }

  onChangeNumOfPass(e){
    this.setState({
      nopass: e.target.value
    });
  }

  onChangeHotel(e){
    this.setState({
      hotel: e.target.value
    });
  }

  onChangeTransp(e){
    this.setState({
      transport: e.target.value
    });
  }

  onChangeGuide(e){
    this.setState({
      tourGuide: e.target.value
    });
  }

  onChangeTot(e){
    this.setState({
      totPrice : e.target.value
    });
  }

  onSubmit(e){
    e.preventDefault();
    const obj = {
      name: this.state.name,
      packId: this.state.packId,
      destination: this.state.destination,
      numofdays : this.state.numofdays ,
      nopass: this.state.nopass,
      hotel: this.state.hotel,
      transport: this.state.transport,
      tourGuide: this.state.tourGuide,
      totPrice : this.state.totPrice 
     
    }

    axios.put("http://localhost:8070/package/update",obj).then(()=>{
      alert("Package Updated!")
    }).catch((err)=>{
        alert(err)
    })
  }


 render() {
  return (
    <div className="container">
    <form onSubmit={sendData}>
    <h1>Please Enter New Tour Package Details</h1><br></br>
    <div className="form-group">
      <label for="Name">Package Name</label>
      <input type="text" class="form-control" id="pacName" placeholder="Enter Package Name here"
      onChange={(e)=> {

        setName(e.target.value);

       } }/>
  
    </div><br></br>
    
    <div className="form-group">
      <label for="id">Package ID</label>
      <input type="text" class="form-control" id="packId" placeholder="Enter Package ID here"
       onChange={(e)=> {

        setPid(e.target.value);

       } }/>
      
      
  
    </div><br></br>
  
    <div className="form-group">
      <label for="desti">Destination</label>
      <input type="text" class="form-control" id="desti" placeholder="Enter Your Destination here"
      onChange={(e)=> {

        setDesti(e.target.value);

       } }/>
      
  
    </div><br></br>

    <div className="form-group">
      <label for="numDays">Number Of Days</label>
      <input type="text" class="form-control" id="numDays" placeholder="Enter Your Number Of Days"
      onChange={(e)=> {

        setDays(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group">
      <label for="numPass">Number Of Passengers</label>
      <input type="text" class="form-control" id="numPass" placeholder="Enter Your Number Of Passengers"
      onChange={(e)=> {

        setPass(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group">
      <label for="hotel">Hotel</label>
      <input type="text" class="form-control" id="hotel" placeholder="Enter Hotel Name"
      onChange={(e)=> {

        setHotel(e.target.value);

       } }/>

    
    </div><br></br>


    <div className="form-group">
      <label for="transport">Transport</label>
      <input type="text" class="form-control" id="transport" placeholder="Enter Transport Company"
      onChange={(e)=> {

        setTrans(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group">
      <label for="guide">Tour Guide</label>
      <input type="text" class="form-control" id="guide" placeholder="If Tour Guide With/Without?"
      onChange={(e)=> {

        setGuide(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group">
      <label for="price">Total Price</label>
      <input type="text" class="form-control" id="price" placeholder="Enter Total Price"
      onChange={(e)=> {

        setPrice(e.target.value);

       } }/>

    
    </div><br></br>

   
    <button type="submit" class="btn btn-success">Add Package</button>
   </form>
   </div>
  );
 }
}
