import React,{useState} from "react";
//import React,{Component} from "react"
import axios from "axios";
import pic2 from '../images/badulla.jpg'

//import axios from "axios";
//import {setErrors} from "./../components/setErrors";
 







function AddVehicle(){

  const [name,setName] = useState(""); 
  const [nic,setNic] = useState("");
  const [vehiCateg,setCateg] = useState("");
  const [vehiNum,setNum] = useState("");
  const [address,setAddres] = useState("");
  const [phone,setMobile] = useState("");
 



  // (local function) sendData(e: any):void
  function sendData(e){
   e.preventDefault();
   
   const newPackage={

   name,
   nic,
   vehiCateg,
   vehiNum,
   address,
   phone



   }
   axios.post("http://localhost:8070/vehicle/add",newVehicle).then(()=>{

     alert("Your Package Creation Is Successfull....!!!")
   }).catch((err)=>{

     alert(err)

   })

  }

 return(
   

   <div className="container" class="p-3 mb-2 bg-dark" >
     <div class="row">

    <form onSubmit={sendData} class="p-3 mb-2 bg-dark text-light">
    <h1 class="p-3 mb-2 bg-success text-white"><cental><strong>Please Enter New Tour Package Details</strong></cental></h1><br></br>
    
     <img  src={pic2} height={600} width={500} style={{position:"absolute",top:300,right:150}} alt="Card image cap" />
    <div class="form-group col-md-6">
      <label for="uname"><strong>User name</strong></label>
      <input type="text" class="form-control" id="uname" placeholder="Enter your name here"
      onChange={(e)=> {

        setName(e.target.value);

       } }/>
  
    </div><br></br>
    
    <div  class="form-group col-md-6">
      <label for="emial"><strong>Email</strong></label>
      <input type="email" class="form-control" id="email" placeholder="Eg- jhone99@gmail.com"
       onChange={(e)=> {

        setMail(e.target.value);

       } }/>
      
      
  
    </div><br></br>
  
    <div class="form-group col-md-6">
      <label for="Mobi"><strong>Mobile Number</strong></label>
      <input type="mobile" class="form-control" id="Mobi" placeholder="Enter your mobile number"
      onChange={(e)=> {

        setPhone(e.target.value);

       } }/>
      
  
    </div><br></br>

    <div class="form-group col-md-6">
      <label for="Country"><strong>Country</strong></label>
      <input type="text" class="form-control" id="Country" placeholder="Enter your country"
      onChange={(e)=> {

        setCountry(e.target.value);

       } }/>

    
    </div><br></br>

    <div class="form-group col-md-6">
      <label for="date"><strong>Date</strong></label>
      <input type="date" class="form-control" id="date" placeholder="DD/MM/YY"
      onChange={(e)=> {

        setDate(e.target.value);

       } }/>

    
    </div><br></br>

    <div class="form-group col-md-6">
      <label for="hotel"><strong>Pickup Place</strong></label>
      <input type="text" class="form-control" id="place" placeholder="Enter pick up place here"
      onChange={(e)=> {

        setPlace(e.target.value);

       } }/>

    
    </div><br></br>


    <div class="form-group col-md-6">
      <label for="adults"><strong>Adults</strong></label>
      <input type="text" class="form-control" id="adults" placeholder="Number of adults"
      onChange={(e)=> {

        setAddu(e.target.value);

       } }/>

    
    </div><br></br>

    <div class="form-group col-md-6">
      <label for="child"><strong>Childrens</strong></label>
      <input type="text" class="form-control" id="child" placeholder="Number of childs"
      onChange={(e)=> {

        setChild(e.target.value);

       } }/>

    
    </div><br></br>

    <div class="form-group col-md-6">
      <label for="duration"><strong>Duration(Days)</strong></label>
      <input type="text" class="form-control" id="duration" placeholder="Enter duration (Days)"
      onChange={(e)=> {

        setDurat(e.target.value);

       } }/>


</div><br></br>

<div class="form-group col-md-6">
  <label for="note"><strong>Notes - </strong></label>
  <input type="text" class="form-control" id="note" placeholder="Enter any note here"
  onChange={(e)=> {

    setNotes(e.target.value);

   } }/>


    
    </div><br></br>
    <a href="/search" class="btn btn-success" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Previous Page</strong></a>&nbsp;&nbsp;&nbsp;
    <button type="submit" class="btn btn-danger "style={{width:"300px"}}><strong><i className="fa-solid fa-folder-plus"></i>&nbsp;&nbsp;Create My Package</strong></button>&nbsp;&nbsp;
    <a href="/allCuz" class="btn btn-success" style={{width:"300px"}}><strong><i className="fa-solid fa-chevron-right"></i>&nbsp;&nbsp;Manage your package</strong></a>&nbsp;
   
    
   </form>
   </div>
   </div>

  


 )




} 

export default AddVehicle
