import React, { useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import {
  useNavigate,
  useLocation,
  useParams,
} from "react-router-dom";


const EditPackage = () => {
  const params = useParams();
  let navigate = useNavigate();
  let location = useLocation();
  const [packageDetails, setPackageDetails] = React.useState({});
  const [updatedPackageDetails, setUpdatedPackageDetails] = React.useState({});

  useEffect(() => {
    fetchPackage();
  }, [])

  const fetchPackage = async () => {
    const response = await fetch(`http://localhost:8070/feedback/get/${params.id}`);
    const data = await response.json();
    setPackageDetails(data.FeedBack);
  } 

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedPackageDetails({ ...updatedPackageDetails, [name]: value });
  }
 
  


  const updatePackage = (e) => {
    e.preventDefault();
    console.log(updatedPackageDetails);
    axios.put(`http://localhost:8060/feedback/update/${params.id}`, updatedPackageDetails).then((res) => {
      if (res.data.success) {
        alert("package update succesfully");
        navigate("/AllFeedbacks/*" + location.search);
        

      }
    })
  }


  return (
    <div className="container">

        <ul className="navbar-nav">
          <li className="nav-item">
             <Link to="/allPackages">
                        <button type="button" className="w-20 btn-lg btn-outline-primary" style={{textDecoration:'none', float: "right", margin:"0px 10px 10px 10px" }}><i className="fa fa-wpforms"></i>  View Packages </button>
                    </Link>
                    </li>
        </ul>



      <form className="needs-validation" noValidate>
        <div class="form group">
          <label for="Uname" class="form-label">
            Package ID
          </label>
          <input
            type="text"
            disabled
            class="form-control"
            name="Uname"
            id="Uname"
            placeholder="Enter Name"
            defaultValue={packageDetails.Uname}
            onChange={handleInputChange}
          />
        </div>
        <div class="form group">
          <label for="pId" class="form-label">
            Package ID
          </label>
          <input
            type="text"
            class="form-control"
            name="pId"
            id="pId"
            placeholder="Enter package ID"
            defaultValue={packageDetails.pId}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="suggess" class="form-label">
            Suggestions
          </label>
          <input
            type="text"
            class="form-control"
            id="suggess"
            name='suggess'
            placeholder="Any suggestions"
            defaultValue={packageDetails.suggess}
            onChange={handleInputChange}
          />
        </div> 

        <div class="form group">
          <label for="fande" class="form-label">
            Faliors and errors
          </label>
          <input
            type="text"
            class="form-control"
            id="fande"
            name='fande'
            placeholder="Faliors and Errors"
            defaultValue={packageDetails.fande}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="rate" class="form-label">
           Give your Rate (x/5)
          </label>
          <input
            type="text"
            class="form-control"
            id="rate"
            name='rate'
            placeholder="Enter your rate here"
            defaultValue={packageDetails.rate}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="date" class="form-label">
            Other Details
          </label>
          <input
            type="text"
            class="form-control"
            id="date"
            name='date'
            placeholder="Enter Date Here"
            defaultValue={packageDetails.date}
            onChange={handleInputChange}
          />
        </div>
        
        <button type="submit" class="btn btn-success" style={{ marginTop: '16px' }}   onClick={updatePackage}> <i className="far fa-check-square"></i>  &nbsp;update  </button>
        
      </form>
    </div>
  );
}
  
export default EditPackage;
