import React,{useState} from "react";
//import React,{Component} from "react"
import axios from "axios";

//import axios from "axios";
//import {setErrors} from "./../components/setErrors";
 







function CusPackage(){

  const [name,setName] = useState("");
  const [email,setMail] = useState("");
  const [phone,setPhone] = useState("");
  const [country,setCountry] = useState("");
  const [arriDate,setDate] = useState("");
  const [pickPlace,setPlace] = useState("");
  const [numofAdd,setAddu] = useState("");
  const [numOfChild,setChild] = useState("");
  const [duration,setDurat] = useState("");
  const [notes,setNotes] = useState("");


  // (local function) sendData(e: any):void
  function sendData(e){
   e.preventDefault();
   
   const newPackage={

    name,
    email,
    phone,
    country,
    arriDate,
    pickPlace,
    numofAdd,
    numOfChild, 
    duration,
    notes

   }
   axios.post("http://localhost:8070/cusPack/add3",newPackage).then(()=>{

     alert("Your Package Creation Is Successfull....!!!")
   }).catch((err)=>{

     alert(err)

   })

  }

 return(
   

   <div className="container" class="p-3 mb-2 bg-dark" >
     <div class="row">

     
    <form onSubmit={sendData} class="p-3 mb-2 bg-dark text-light">
    <h1 class="p-3 mb-2 bg-primary text-white"><cental><strong>Please Enter New Tour Package Details</strong></cental></h1><br></br>
    
    <div className="form-group" style={{width:"500px" ,}} class="form-group col-md-6">
      <label for="uname"><strong>user name</strong></label>
      <input type="text" class="form-control" id="uname" placeholder="Enter Your Name here"
      onChange={(e)=> {

        setName(e.target.value);

       } }/>
  
    </div><br></br>
    
    <div  style={{width:"500px"}} class="form-group col-md-6">
      <label for="emial"><strong>Email</strong></label>
      <input type="text" class="form-control" id="email" placeholder="Enter Your Email Address here"
       onChange={(e)=> {

        setMail(e.target.value);

       } }/>
      
      
  
    </div><br></br>
  
    <div className="form-group" style={{width:"500px"}}>
      <label for="Mobi"><strong>Mobile Number</strong></label>
      <input type="text" class="form-control" id="Mobi" placeholder="Enter Your Mobile Number"
      onChange={(e)=> {

        setPhone(e.target.value);

       } }/>
      
  
    </div><br></br>

    <div className="form-group" style={{width:"500px"}}>
      <label for="Country"><strong>Country</strong></label>
      <input type="text" class="form-control" id="Country" placeholder="Enter your country"
      onChange={(e)=> {

        setCountry(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"500px"}}>
      <label for="date"><strong>Date</strong></label>
      <input type="text" class="form-control" id="date" placeholder="DD/MM/YY"
      onChange={(e)=> {

        setDate(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"500px"}}>
      <label for="hotel"><strong>Pickup Place</strong></label>
      <input type="text" class="form-control" id="place" placeholder="Enter Pick up place here"
      onChange={(e)=> {

        setPlace(e.target.value);

       } }/>

    
    </div><br></br>


    <div className="form-group" style={{width:"500px"}}>
      <label for="adults"><strong>Adults</strong></label>
      <input type="text" class="form-control" id="adults" placeholder="Number of adults"
      onChange={(e)=> {

        setAddu(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"500px"}}>
      <label for="child"><strong>Childrens</strong></label>
      <input type="text" class="form-control" id="child" placeholder="Number of childs"
      onChange={(e)=> {

        setChild(e.target.value);

       } }/>

    
    </div><br></br>

    <div className="form-group" style={{width:"500px"}}>
      <label for="duration"><strong>Duration(Days)</strong></label>
      <input type="text" class="form-control" id="duration" placeholder="Enter Duration(Days)"
      onChange={(e)=> {

        setDurat(e.target.value);

       } }/>


</div><br></br>

<div className="form-group" style={{width:"500px"}}>
  <label for="note"><strong>Notes - </strong></label>
  <input type="text" class="form-control" id="note" placeholder="Enter any note here"
  onChange={(e)=> {

    setNotes(e.target.value);

   } }/>


    
    </div><br></br>
    <a href="/search" class="btn btn-primary" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Previous Page</strong></a>&nbsp;
    <button type="submit" class="btn btn-danger "style={{width:"300px"}}><strong><i className="fa-solid fa-folder-plus"></i>&nbsp;&nbsp;Create My Package</strong></button>&nbsp;
    <a href="/allCuz" class="btn btn-light" style={{width:"300px"}}><strong><i className="fa-solid fa-chevron-right"></i>&nbsp;&nbsp;Manage your package</strong></a>&nbsp;
   
    
   </form>
   </div>
   </div>

  


 )




} 

export default CusPackage
