import React,{Component} from "react";
import axios from "axios";
import {Link} from 'react-router-dom';
import tour from '../images/tour.jpg'

//import Package from "../../../TourPackage/BACKEND/models/Package";



export default class S_AllCusPacz extends Component{


   constructor(props){

       super(props);

       this.state={

       cusPacks:[]
       };

   }
  componentDidMount(){

   this.retriveCusPacks();
  }
  retriveCusPacks(){

   axios.get("http://localhost:8070/cusPack/all4").then(res=>{

      if(res.data.success){
          this.setState({

            cusPacks:res.data.existingCusPacks

          });

          console.log(this.state.cusPacks)

      }


   })



  }



  
  
  onDelete(id){

     fetch(`http://localhost:8070/cusPack/delete/${id}`,{

           method:`DELETE`




     }).then((result)=>{

         result.json().then((resp)=>{

           console.warn(resp)
           alert("Package Cancelled...!!")
           this.retriveCusPacks()

         })


     }) 
    


  }

  //Filter /Search Mechod
 filterContent(cusPacks,searchTerm){

  const results=cusPacks.filter((cusPacks)=>cusPacks.name.toLowerCase().includes(searchTerm));
  this.setState({cusPacks:results});

}



handleTextSearch=(e)=>{

   const searchTerm=e.currentTarget.value;
   axios.get("http://localhost:8070/cusPack/all4").then(res=>{

    if(res.data.success){
       this.filterContent(res.data.existingCusPacks,searchTerm)
    }
});

}; 





  
 render(){

    return(

      <div className="container" class="p-3 mb-2 bg-dark text-white">
         
    
<div>
 <div> 
<img  src={tour} style={{ position:"abs", right:2000,top:20}} height={300} width={800}  alt="Card image cap"></img>
<div class="card-img-overlay" style={{ position:"absolute", right:200,top:70}}>
 <h3 class="text-white bg-dark" style={{ width:400}}><strong>Find your tour package</strong>-</h3> 
 <div  className="col-lg-3 mt-2 mb-2" class="text-center"  style={{ width:400}}>
 <input
              className="form-control"
              type="search"
              placeholder="Enter User Name Here"
              name="searchTerm"
              onChange={this.handleTextSearch}
             
             ></input></div>
 </div>  </div>    
       
      <br></br>
     </div>

     
      <table class="table table-dark table-hover">
      <thead>
      <tr class="text-white bg-dark">
          <th scope="col">Count</th>
           <th scope="col">Name</th>
           <th scope="col">Email</th>
           <th scope="col">Phone</th>
           <th scope="col">Country</th>
           <th scope="col">ArrivalDate</th>
           <th scope="col">PickUpPlace</th>
           <th scope="col">NumberOfAdults</th>
           <th scope="col">NumberOfChilds</th>
           <th scope="col">Duration(Days)</th>
           <th scope="col">Notes</th>
           <th scope="col">Action</th>

        
  
      </tr>
     </thead>
     <tbody>

       {this.state.cusPacks.map((cusPacks,index)=>(

        <tr>
          <th scope="row">{index+1}</th>
          <td>{cusPacks.name}</td>
          <td>{cusPacks.email}</td>
          <td>{cusPacks.phone}</td>
          <td>{cusPacks.country}</td>
          <td>{cusPacks.arriDate}</td>
          <td>{cusPacks.pickPlace}</td>
          <td>{cusPacks.numofAdd}</td>
          <td>{cusPacks.numOfChild}</td>
          <td>{cusPacks.duration}</td>
          <td>{cusPacks.notes}</td>
      
          <td>
         <a className="btn btn-success"  href={`/updCuz/${cusPacks._id}`}>Update</a>&nbsp;
            </td>       <br></br>             
         <td> <a class="btn btn-danger" onClick={()=>this.onDelete(cusPacks._id)}>&nbsp;Cancel</a></td>
         

        </tr>

       ))}



     </tbody>



     </table>
     <a href="/allCuz" class="btn btn-info" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Priviouse Page</strong></a>
   
    </div> 
     

  

    ) 
 }

 }
