//import React from "react";
import React,{Component} from "react";
//import React, {Component} from "react";
import axios from 'axios';
import '../App.css';
//import "bootstrap/dist/css/bootstrap.min.css";


 export default class editPack extends Component{

  constructor(props){
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePackId = this.onChangePackId.bind(this);
    this.onChangeDestination = this.onChangeDestination.bind(this);
    this.onChangeNumOfDays = this.onChangeNumOfDays.bind(this)
    this.onChangeNumOfPass = this.onChangeNumOfPass.bind(this)
    this.onChangeHotel = this.onChangeHotel.bind(this)
    this.onChangeTransp = this.onChangeTransp.bind(this)
    this.onChangeGuide = this.onChangeGuide.bind(this)
    this.onChangeTot = this.onChangeTot.bind(this)
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      packages:[]
      // card_num: '',
      // full_name: '',
      // cvv: '',
      // expiry_date: '',
      // street_address: '',
      // country: '',
      // city: '',
      // zip_code: '',
      // state: ''
    }
  }

  componentDidMount() {
    this.retrivePackages();
  }

  retrivePackages(){
    axios.get("http://localhost:8070/package/all")
      .then(response => {
        console.log(response.data);
        this.setState({packages:response.data.Package})
      })
      .catch(function (error){
        console.log(error);
    })
  }

  onChangeName(e){
    this.setState({
      name: e.target.value
    });
  }

  onChangePackId(e){
    this.setState({
      packId: e.target.value
    });
  }

  onChangeDestination(e){
    this.setState({
      destination: e.target.value
    });
  }

  onChangeNumOfDays(e){
    this.setState({
      numofdays: e.target.value
    });
  }

  onChangeNumOfPass(e){
    this.setState({
      nopass: e.target.value
    });
  }

  onChangeHotel(e){
    this.setState({
      hotel: e.target.value
    });
  }

  onChangeTransp(e){
    this.setState({
      transport: e.target.value
    });
  }

  onChangeGuide(e){
    this.setState({
      tourGuide: e.target.value
    });
  }

  onChangeTot(e){
    this.setState({
      totPrice : e.target.value
    });
  }

  onSubmit(e){
    e.preventDefault();
    const obj = {
      name: this.props.name,
      packId: this.state.packId,
      destination: this.state.destination,
      numofdays : this.state.numofdays ,
      nopass: this.state.nopass,
      hotel: this.state.hotel,
      transport: this.state.transport,
      tourGuide: this.state.tourGuide,
      totPrice : this.state.totPrice 
     
    }

    axios.put("http://localhost:8070/package/update",obj).then(()=>{
      alert("Package Updated!")
    }).catch((err)=>{
        alert(err)
    })
  }


 render() {
  return (
    <><div className="App">
          <h1>Details can update from here.</h1>
      </div><div>
              <div class="card p-3 border-0 horizontal-center" style={{ maxWidth: "70%" }}>
                  <h2 class="text-center margin-bottom"><strong>Enter Package details</strong></h2>
                  <hr class="divider-line" style={{ marginTop: "10px" }} />
                  <br></br>
                  <form class="formt" onSubmit={this.onSubmit}>
                      <div class="inputbox mt-3">
                          <input type="text" name="name" class="form-control" required="required" placeholder="Package Name" defaultValue={this.state.packages.name}
                              onChange={this.onChangeName} />
                      </div>
                      <div class="inputbox mt-3 mr-2">
                          <input type="text" name="pack-ID" placeholder="Package ID" size="18" id="cr_no" minlength="19" maxlength="19" class="form-control" defaultValue={this.state.packages.packId}
                              onChange={this.onChangePackId} />
                          <i class="fa fa-credit-card"></i>
                      </div>

                      <div class="row">
                          <div class="col-md-6">
                              <div class="d-flex flex-row">
                                  <div class="inputbox mt-3 mr-2">
                                      <input name="expiryD"
                                          class="form-control" required="required" placeholder="Destination"
                                          type="text" pattern="\d*" maxlength="5" defaultValue={this.state.packages.destination}
                                          onChange={this.onChangeDestination} />
                                  </div>
                                  <div class="inputbox mt-3 mr-2 margin-left">
                                      <input name="cvv"
                                          class="form-control"  required="required" placeholder="Number of days"
                                          type="text" pattern="\d*" maxlength="3" defaultValue={this.state.packages.numofdays}
                                          onChange={this.onChangeNumOfDays} />
                                  </div>
                              </div>
                          </div>
                      </div>
                      <div class="mt-4 mb-4">
                          <h5 class="margin-bottom">Billing Address</h5>
                          <hr class="divider-line" style={{ marginTop: "10px" }} />
                          <br></br>
                          <div class="">
                              <div class="inputbox " >
                                  <input type="text" name="name" class="form-control" required="required" placeholder="Num of passengers" defaultValue={this.state.packages.nopass}
                                      onChange={this.onChangeNumOfPass} />
                              </div>
                          </div>
                        
                          <div class="">
                              <div class="inputbox ">
                                  <input type="text" name="name" class="form-control" required="required" placeholder="Hotel" defaultValue={this.state.packages.hotel}
                                      onChange={this.onChangeHotel} />
                              </div>
                          </div>
                          <div class="row mt-2">
                              <div class="col-5">
                                  <div class="inputbox mt-3 mr-2">
                                      <input type="text" name="name" class="form-control" required="required" placeholder="Transport" defaultValue={this.state.packages.transport}
                                          onChange={this.onChangeTransp} />
                                  </div>
                              </div>
                              <div class="col">
                                  <div class="inputbox mt-3 mr-2">
                                      <input type="text" name="name" class="form-control" required="required" placeholder="Tour Guide" defaultValue={this.state.packages.tourGuide}
                                          onChange={this.onChangeGuide} />
                                  </div>
                              </div>
                              <div class="col">
                                  <div class="inputbox mt-3 mr-2">
                                      <input type="text" name="name" class="form-control" required="required" placeholder="Total price" defaultValue={this.state.packages.totPrice}
                                          onChange={this.onChangeTot} />
                                  </div>
                              </div>
                          </div>
                      </div>
                  </form>
                  <div class="text-center" style={{ marginTop: "-30px" }}>
                      <button className="btn btn-primary btn-lg" onClick={this.onSubmit}>
                          UPDATE
                      </button>
                  </div>
              </div>
          </div></>
  );
 }
}
