import React,{useEffect, useState} from "react"
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";





export default function EditElectricity(){

  const [pannelSize,setpannelSize] = useState("");
    const [company,setcompany] = useState("");
    const [intensity,setintensity] = useState("");
    const [monthlyBill,setmonthlyBill] = useState("");
    const [units,setunits] = useState("");
    const [irradiance,setirradiance] = useState("");
    const [conversionEfficiency,setconversionEfficiency] = useState("");

  const { id } = useParams();

  useEffect(() => {
    getElectricities();
  }, []);


  function getElectricities() {
    let company = true;

// retriew data for id

    fetch(`http://localhost:8070/electricity/get/${id}`)
      .then((res) => res.json())

      .then((result) => {
        if (electricity) {
          setpannelSize(result.pannelSize);
          setcompany(result.company);
          setintensity(result.intensity);
          setmonthlyBill(result.monthlyBill);
          setunits(result.setunits);
         
        }
        console.log(result);
      });

    return () => (electricity = false);
  }

   // update data 
  function updateData(e){

      e.preventDefault();

      //alert("Insert");

      const updateElectricity ={
        pannelSize,
    company,
    intensity,
    monthlyBill,
    units,
    irradiance,
    conversionEfficiency,
     
    
      }

      axios
    .patch(`http://localhost:8070/company/electricity/${id}`, updateElectricity)
    .then((_res) => {
      alert("Data Updated Successfully!");
     // navigate("/front");
      console.log(updateElectricity);
    })
    .catch((_err) => {
      
      alert("Database Error");
    });
  }

return(

  <div className="container" class="back-img">
  <div class ="card-company">

<div class="text-dark">
   <center><h1><strong><u> SAVING POWER AND MONEY </u></strong></h1></center><br></br>
</div>

<form className =  "company" >
<div className="form-group">
<label for="pannelSize"><strong>Pannel Size</strong></label>
<input type="text" class="form-control" id="Name" required placeholder="Enter your company name here" value={Name}
onChange={(e)=>{setName(e.target.value);}}
/>
</div>
<br></br>

<div className="form-group">
<label for="Contact_Number"><strong>Your Contact Number</strong></label>
<input type="int" class="form-control" id="Contact_Number" required placeholder="Enter contact number here" value={Contact_Number}
onChange={(e)=> {setContact_Number(e.target.value);} }
 />
</div>
<br></br>

<div className="form-group">
<label for="Location"><strong>Location</strong></label>
<input type="text" class="form-control" id="Location" required placeholder="Enter your location here" value={Location}
onChange={(e)=> {setLocation(e.target.value);} }
/>
</div>
<br></br>

<div className="form-group">
<label for="Email"><strong>Company Email</strong></label>
<input type="text" class="form-control" id="Email" required placeholder="Enter your company Email here" value={Email}
onChange={(e)=> {setEmail(e.target.value);} }
/>
</div>
<br></br>

<div class="d-grid gap-2 col-6 mx-auto">
<button type="button" class="btn btn-warning" onClick={updateData}><strong> Update Details </strong></button>
<div>
<a href="/all" class="btn btn-warning" style={{width:"300px"}}><strong>View Details</strong></a>
</div>
</div>

</form>
</div>
</div>

)

}
