import React,{useEffect, useState} from "react"
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";



export default function EditCusPackz(){

    const [name,setName] = useState("");
    const [email,setMail] = useState("");
    const [phone,setPhone] = useState("");
    const [country,setCountry] = useState("");
    const [arriDate,setDate] = useState("");
    const [pickPlace,setPlace] = useState("");
    const [numofAdd,setAddu] = useState("");
    const [numOfChild,setChild] = useState("");
    const [duration,setDurat] = useState("");
    const [notes,setNotes] = useState("");

  const { id } = useParams();

  useEffect(() => {
      getCusPacks();
    }, []);
  
    //let navigate= useNavigate();
  
    function getCusPacks() {
      let cusPack = true;
  
      fetch(`http://localhost:8070/cusPack/get/${id}`)
        .then((res) => res.json())
  
        .then((result) => {
          if (cusPack) {
            setName(result.name);
            setMail(result.email);
            setPhone(result.phone);
            setCountry(result.country);
            setDate(result.arriDate);
            setPlace(result.pickPlace);
            setAddu(result.numofAdd);
            setChild(result.numOfChild);
            setDurat(result.duration);
            setNotes(result.notes);
           
          }
          console.log(result);
        });
  
      return () => (cusPack = false);
    }

  
  function updateData(e){

      e.preventDefault();

      //alert("Insert");

      const updateCusPacks ={
        name,
       email,
       phone,
       country,
       arriDate,
       pickPlace,
       numofAdd,
       numOfChild, 
       duration,
       notes
      }

      axios
    .patch(`http://localhost:8070/cusPack/update/${id}`, updateCusPacks)
    .then((_res) => {
      alert("Your Package Updated Successfully!");
     // navigate("/front");
      console.log(updateCusPacks);
    })
    .catch((_err) => {
      
      alert("Database Error");
    });
  }



return(
    <div className="container">
        <h2>Update User Package Details</h2>
        <form className =  "CusPackages">
        <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputEmail4">User Name</label>
                    <input type="text" className="form-control" id="uname" placeholder="User Name" value={name} onChange={(e)=>{setName(e.target.value);}}/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="iinputPid">Email</label>
                    <input type="text" className="form-control" id="Pid" placeholder="sithumgamage@gmail/yahoo.com" value={email} onChange={(e)=>{setMail(e.target.value);} }/>
                </div>
            </div>

            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputSugges">Mobile Number</label>
                    <input type="text" className="form-control" id="suggest" placeholder="+94 077 XXX XXXX" value={phone} onChange={(e)=>{setPhone(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputFande">Your conuntry</label>
                    <input type="text" className="form-control" id="fande" placeholder="Enter your country" value={country} onChange={(e)=>{setCountry(e.target.value);} }/>
                </div>
            </div>

          
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Arrival Date</label>
                    <input type="text" className="form-control" id="rate" placeholder="DD/MM/YYYY" value={arriDate} onChange={(e)=>{setDate(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Pick Up Place</label>
                    <input type="text" className="form-control" id="date" placeholder="Eg-Colombo,Kandy,Monaragala" value={pickPlace}  onChange={(e)=>{setPlace(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Number Of Adults</label>
                    <input type="text" className="form-control" id="rate" placeholder="E.g-1,3,4.." value={numofAdd} onChange={(e)=>{setAddu(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Number Of Children</label>
                    <input type="text" className="form-control" id="date" placeholder="E.g-1,3,4.." value={numOfChild}  onChange={(e)=>{setChild(e.target.value);} }/>
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputRate">Tour Duration(Days)</label>
                    <input type="text" className="form-control" id="rate" placeholder="You can create Maximum 10 days packages" value={duration} onChange={(e)=>{setDurat(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputDate">Additional Note</label>
                    <input type="text" className="form-control" id="date" placeholder="" value={notes}  onChange={(e)=>{setNotes(e.target.value);} }/>
                </div>
            </div>

        
            
        </form><br></br><div>
                 <a href="/allCuz" class="btn btn-success" style={{width:"300px"}}><strong><i className="fa-solid fa-user-clock"></i>&nbsp;&nbsp;Priviouse Page</strong></a>&nbsp;&nbsp;
                <button  type="button" class="btn btn-danger"   onClick={updateData}> <i className="fa-solid fa-wrench "></i>&nbsp;&nbsp; Update My Package&nbsp;&nbsp;&nbsp;</button>&nbsp;&nbsp;
            
            </div>
            
            
    </div>
)

}
