import React, { useEffect } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import {

  useParams,
} from "react-router-dom";
import AllFeedbacks from './AllFeedBacks';

const EditFeedback = () => {
  const params = useParams();
  const [packageDetails, setPackageDetails] = React.useState({});
  const [updatedPackageDetails, setUpdatedPackageDetails] = React.useState({});

  useEffect(() => {
    fetchPackage();
  }, [])

  const fetchPackage = async () => {
    const response = await fetch(`http://localhost:8070/feedback/get/627fc52f08a21f1797a391d7`);
    const data = await response.json();
    setPackageDetails(data.feedbacks);
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedPackageDetails({ ...updatedPackageDetails, [name]: value });
  }

  const updatePackage = (e) => {
    e.preventDefault();
    console.log(updatedPackageDetails);
    axios.put(`http://localhost:8070/feedback/update/627fc52f08a21f1797a391d7}`, updatedPackageDetails).then((res) => {
      if (res.data.success) {
        alert("package update succesfully");
        this.retriveFeedbacks();
      }
    })
  }


  return (
    <div className="container">

        <ul className="navbar-nav">
          <li className="nav-item">
        
                    </li>
        </ul>



      <form className="needs-validation" noValidate>
        <div class="form group">
          <label for="Uname" class="form-label">
            User name
          </label>
          <input
            type="text"
            disabled
            class="form-control"
            name="Uname"
            id="Uname"
            placeholder="Enter Package ID"
            defaultValue={packageDetails.Uname}
            onChange={handleInputChange}
          />
        </div>
        <div class="form group">
          <label for="pId" class="form-label">
            Package ID
          </label>
          <input
            type="text"
            class="form-control"
            name="pId"
            id="pId"
            placeholder="Enter Package ID"
            defaultValue={packageDetails.pId}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="suggess" class="form-label">
           Suggestions
          </label>
          <input
            type="text"
            class="form-control"
            id="suggess"
            name='suggess'
            placeholder="Enter Suggestions"
            defaultValue={packageDetails.suggess}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="fande" class="form-label">
            Faults and Errors
          </label>
          <input
            type="text"
            class="form-control"
            id="fande"
            name='fande'
            placeholder="Enter Faults and Errors"
            defaultValue={packageDetails.fande}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="rate" class="form-label">
            Price Range
          </label>
          <input
            type="text"
            class="form-control"
            id="rate"
            name='rate'
            placeholder="Give Your Rate Here(x/5)"
            defaultValue={packageDetails.rate}
            onChange={handleInputChange}
          />
        </div>

        <div class="form group">
          <label for="date" class="form-label">
           Date
          </label>
          <input
            type="text"
            class="form-control"
            id="date"
            name='date'
            placeholder="Enter Date here"
            defaultValue={packageDetails.date}
            onChange={handleInputChange}
          />
        </div>



        
        
        <button type="submit" class="btn btn-success"    onClick={updatePackage}> <i className="far fa-check-square"></i>  &nbsp;update  </button>
        
      </form>
    </div>
  );
}

export default EditFeedback;
