import React,{Component} from "react";
import axios from "axios";
//import Package from "../../../TourPackage/BACKEND/models/Package";



export default class EditPack extends Component{


   constructor(props){

       super(props);

       this.state={

        packages:[]
       };

   }
  componentDidMount(){

   this.retrivePackages();
  }
  retrivePackages(){

   axios.get("http://localhost:8070/package/all").then(res=>{

      if(res.data.success){
          this.setState({

            packages:res.data.existingPackages

          });

          console.log(this.state.packages)

      }


   })



  }


//OnDelete Method
  /*
    onDelete=(id)=>{
      

     axios.delete(`/package/delete2/${id}`).then((res)=>{


      alert(res.data.title+"Delete Succsessfully");
       


     });



  }; */
     
  onDelete(id){

     fetch(`http://localhost:8070/package/delete/${id}`,{

           method:`DELETE`




     }).then((result)=>{

         result.json().then((resp)=>{

           console.warn(resp)
           alert("Deleted Succsessfull")
           this.retrivePackages()

         })


     })
    


  }



 render(){

    return(

      <div className="container">

      <p>All Packages</p>
      <table className="table table-hover" style={{marginTop:'40px'}}>
      <thead>
      <tr>
           <th scope="col">Count</th>
           <th scope="col">Package Name</th>
           <th scope="col">Package ID</th>
           <th scope="col">Destination</th>
           <th scope="col">NumberofDays</th>
           <th scope="col">NumberofPassengers</th>
           <th scope="col">Hotel</th>
           <th scope="col">Transport</th>
           <th scope="col">TourGuide</th>
           <th scope="col">TotalPrice(Rs)</th>
           <th scope="col">Action</th>
  
  
      </tr>
     </thead>
     <tbody>

       {this.state.packages.map((packages,index)=>(

        <tr>
          <th scope="row">{index+1}</th>
          <td>{packages.name}</td>
          <td>{packages.packId}</td>
          <td>{packages.destination}</td>
          <td>{packages.numofdays}</td>
          <td>{packages.nopass}</td>
          <td>{packages.hotel}</td>
          <td>{packages.transport}</td>
          <td>{packages.tourGuide}</td>
          <td>{packages.totPrice}</td>

          <td>
             <a className="btn btn-warning" href="#" >
             <i className="fas fa-edit"></i>&nbsp;Edit
             </a>
             &nbsp;
             
             <button onClick={()=>this.onDelete(packages._id)}>Delete</button>
            
             


          </td>



        </tr>

       ))}


     </tbody>
     </table>
     <div>
      <input type="text" value={nam }    /><br/><br/>
      <input type="text" value={packId}     /><br/><br/>
      <input type="text" value={destination}       /><br/><br/>
      <input type="text" value={numofdays}      /><br/><br/>
      <input type="text" value={nopass}       /><br/><br/>
      <input type="text" value={hotel}      /><br/><br/>
      <input type="text" value={transport}      /><br/><br/>
      <input type="text" value={tourGuide}      /><br/><br/>
      <input type="text" value={ totPrice}       /><br/><br/>
      <button onClick={updatePackage}>Update Package</button>
      
    </div>

      </div>



    ) 
 }

 }
