import React,{useEffect, useState} from "react"
import axios from "axios";
import { Link, useNavigate, useParams } from "react-router-dom";

/* Uname,
        pId,
        suggess,
        fande,
        rate,
        date*/

export default function EditFeedback(){

  const [Uname, setUname] = useState("");
  const [pId, setPid] = useState("");
  const [suggess, setSuggess] = useState("");
  const [fande, setFande] = useState("");
  const [rate, setRate] = useState("");
  const [date, setDate] = useState("");


  const { id } = useParams();

  useEffect(() => {
      getFeedbacks();
    }, []);
  
    let navigate= useNavigate();
  
    function getFeedbacks() {
      let feedback = true;
  
      fetch(`http://localhost:8070/feedback/get/${id}`)
        .then((res) => res.json())
  
        .then((result) => {
          if (feedback) {
            setUname(result.Uname);
            setPid(result.pId);
            setSuggess(result.suggess);
            setFande(result.fande);
            setRate(result.rate);
            setDate(result.date);
           
          }
          console.log(result);
        });
  
      return () => (feedback = false);
    }

  
  function updateData(e){

      e.preventDefault();

      //alert("Insert");

      const updateFeedback ={
        Uname,
        pId,
        suggess,
        fande,
        rate,
        date
      }

      axios
    .patch(`http://localhost:8070/feedback/update/${id}`, updateFeedback)
    .then((_res) => {
      alert("Payment Updated Successfully!");
      navigate("/front");
      console.log(updateFeedback);
    })
    .catch((_err) => {
      
      alert("Database Error");
    });
  }



return(
    <div className="bg-back1">
        <h2>Update Feedback Details</h2>
        <form className =  "payment">
        <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputEmail4">FIRST NAME</label>
                    <input type="text" className="form-control" id="firstName" placeholder="First name" value={firstName} onChange={(e)=>{setFisrtName(e.target.value);}}/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputPassword4">LAST NAME</label>
                    <input type="text" className="form-control" id="lastName" placeholder="Last Name" value={lastName} onChange={(e)=>{setLastName(e.target.value);} }/>
                </div>
            </div>

            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputEmail4">EMAIL ADDRESS</label>
                    <input type="email" className="form-control" id="email" placeholder="Email Address" value={email} onChange={(e)=>{setEmail(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputPassword4">PHONE NUMBER</label>
                    <input type="number" className="form-control" id="phoneNumber" placeholder="Phone Number" value={phoneNumber} onChange={(e)=>{setPhoneNumber(e.target.value);} }/>
                </div>
            </div>

            <div className="form-group">
                <label htmlFor="inputAddress">ADDRESS</label>
                <input type="text" className="form-control" id="AddressOne" placeholder="Main St" value={AddressOne} onChange={(e)=>{setAddressOne(e.target.value);} }/><br></br>
                <input type="text" className="form-control" id="AddressTwo" placeholder="Apartment, studio, or floor" value={AddressTwo} onChange={(e)=>{setAddressTwo(e.target.value);} }/>
            </div>
            <div className="form-row">
                <div className="form-group col-md-6">
                    <label htmlFor="inputCity">TOWN/CITY</label>
                    <input type="text" className="form-control" id="townCity" placeholder="Town/City" value={townCity} onChange={(e)=>{setTownCity(e.target.value);} }/>
                </div>
                <div className="form-group col-md-6">
                    <label htmlFor="inputCity">COUNTRY</label>
                    <input type="text" className="form-control" id="country" placeholder="Country" value={country}  onChange={(e)=>{setCountry(e.target.value);} }/>
                </div>
            </div>

            <div className="card-footer">
                <button className="btn btn-sm btn-success float-right" type="submit" onClick={updateData}>
                <i className="mdi mdi-gamepad-circle"></i>Save</button>
                <button className="btn btn-sm btn-danger" type="reset">
                <i className="mdi mdi-lock-reset"></i> Cancel</button>
            </div>
            
        </form>
    </div>
)

}
